import React, { useEffect, useState, useContext } from 'react';
import { Button, Container, Form, FormGroup, Input } from 'reactstrap';
import AppNavbar from './Navbar';
import { Link } from 'react-router-dom';

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import DeleteIcon from '@mui/icons-material/Delete';
import CircularProgress from '@mui/material/CircularProgress';
import FormatContext from '../util/FormatContext';
import '../css/button-styles.css';


/**
 * The FilmTable component contains the functionality of the film table
 *
 * @component
 * @returns {ReactElement} JSX that renders the Navigation Bar, a table listing the films, and a form for searching films.
 *
 * The FilmTable component performs the following:
 * 
 * - useState -  for managing state for the films, loading status, search input, and filtering the films.
 * - useEffect -  for fetching films from the API.
 * - useContext -  for accessing the format context.
 * - handleSearch - to update the search input.
 * - handleSearchSubmit -  to send a search request to the API and update the filtered films state.
 * - clear - to clear the search input and reset the filtered films state.
 * - fetchFilms -  (inside the `useEffect` hook) to fetch films from the API in different formats,update the films and filtered films state.
 * - remove -  to send a delete request to the API and update the states of the film table.
 * - edit - redirects to the update page to send a put request to the API to update a film
 */
const FilmTable = () => {

  const { format } = useContext(FormatContext);

  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(false);

  // search
  const [searchInput, setSearchInput] = useState('');
  const [filteredFilms, setFilteredFilms] = useState([]);
  const [searchType, setSearchType] = useState("title");

  const handleSearch = (e) => {
    setSearchInput(e.target.value);
  };

  // const handleSearchSubmit = (e) => {
  //   e.preventDefault();

  //   const searchText = searchInput.toLowerCase();

  //   fetch(`FilmAPI/filmapi?${searchType}=${searchText}`, {
  //     headers: {
  //       Accept: "application/json",
  //     },
  //     mode: "cors",
  //   })
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setFilteredFilms(data);
  //     });
  // };

  const handleSearchSubmit = (e) => {
    e.preventDefault();

    const searchText = searchInput.toLowerCase();

    // Depending on the format, set the Accept header value
    let accept = "application/json";
    if (format === "xml") {
      accept = "application/xml";
    } else if (format === "text") {
      accept = "text/plain";
    }

    fetch(`FilmAPI/filmapi?${searchType}=${searchText}`, {
      headers: {
        Accept: accept,
      },
      mode: "cors",
    })
      .then(async (response) => {
        // Depending on the format, parse the response differently
        if (format === "json") {
          const data = await response.json();
          setFilteredFilms(data);
        } else if (format === "xml") {
          const text = await response.text();
          const parser = new DOMParser();
          const xmlDoc = parser.parseFromString(text, "application/xml");
          const films = xmlDoc.getElementsByTagName("Film");
          const data = Array.from(films).map((film) => {
            return {
              id: parseInt(film.getElementsByTagName("id")[0].textContent),
              title: film.getElementsByTagName("title")[0].textContent,
              year: parseInt(film.getElementsByTagName("year")[0].textContent),
              director: film.getElementsByTagName("director")[0].textContent,
              stars: film.getElementsByTagName("stars")[0].textContent,
              review: film.getElementsByTagName("review")[0].textContent,
            };
          });
          setFilteredFilms(data);
        } else if (format === "text") {
          const text = await response.text();
          const films = text.trim().split("|").filter(Boolean);
          const data = films.map((film) => {
            const [
              id,
              title,
              year,
              director,
              stars,
              review,
            ] = film.split("#");
            return {
              id: parseInt(id),
              title,
              year: parseInt(year),
              director,
              stars,
              review,
            };
          });
          setFilteredFilms(data);
        }
      });
  };

  // clear search 
  const clear = () => {
    setSearchInput('');
    setFilteredFilms(groups);
  };

  // get films and parse for the table for json, xml + text
  useEffect(() => {
    console.log("Fetching Films in ",format);
    const fetchFilms = async (format) => {
      setLoading(true);

      // set accept
      let accept = "application/json";
      if (format === "xml") {
        accept = "application/xml";
      } else if (format === "text") {
        accept = "text/plain";
      }

      // parse in different formats
      fetch("FilmAPI/filmapi", {
        headers: {
          Accept: accept,
        },
        mode: "cors",
      })
        .then(async (response) => {

          if (format === "text") {
            const text = await response.text();
            const films = text.trim().split("|").filter(Boolean);
            const data = films.map((film) => {
              const [
                id,
                title,
                year,
                director,
                stars,
                review,
              ] = film.split("#");
              return {
                id: parseInt(id),
                title,
                year: parseInt(year),
                director,
                stars,
                review,
              };
            });
            console.log("Text Table");
            return data;


          } else if (format === "xml") {
            const xmlData = await response.text();
            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(xmlData, "application/xml");

            const films = xmlDoc.getElementsByTagName("Film");
            const data = Array.from(films).map((film) => {

              return {
                id: parseInt(film.getElementsByTagName("id")[0].textContent),
                title: film.getElementsByTagName("title")[0].textContent,
                year: film.getElementsByTagName("year")[0].textContent,
                director: film.getElementsByTagName("director")[0].textContent,
                stars: film.getElementsByTagName("stars")[0].textContent,
                review: film.getElementsByTagName("review")[0].textContent,
              };

            });
            console.log("XML Table");
            return data;
          } 
          else {
            console.log("JSON Table");
            return response.json();
          }
        })
        .then((data) => {
          setGroups(data);
          setFilteredFilms(data);
          setLoading(false);

        });
    };

    fetchFilms(format);
  }, [format]);

  // delete
  const remove = async (id, format) => {
    let body = Request.body;
    let accept = "application/json";
    console.log("Deleted in ",FormatContext.format);
    format = FormatContext.format
    if (format === "xml") {
      console.log("format === xml");
      accept = "application/xml";
      body = `<Film><id>${id}</id></Film>`;
    } 

    else if (format === "text") {
      console.log("format === text");
      accept = "text/plain";
      body = `${id}#`;
    }

    else {
      console.log("format === json");
      accept = "application/json";
      body = JSON.stringify({'id': id});
    }

    await fetch(`/FilmAPI/filmapi`, {
      method: 'DELETE',
      headers: {
        'Accept': accept,
        'Content-Type': accept
      },
      body: body
    }).then(() => {
      let updatedGroups = [...groups].filter(i => i.id !== id);
      setGroups(updatedGroups);
      setFilteredFilms(updatedGroups);
    });
  }


  if (loading) {

    return (
      <div className='d-flex justify-content-center d-flex align-items-center'>
        <CircularProgress color = "error"/>
      </div>
    );
  }

  return (
    <div>
      <AppNavbar />
      <Container fluid className = "pt-3">

        <TableContainer component={Paper} className='shadow'>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead
             sx={{
              "& th": {
                color: "white",
                backgroundColor: "#212529"
              }
            }}>
              <TableRow>
                <TableCell><b>ID</b></TableCell>
                <TableCell align="left"><b>Title</b></TableCell>
                <TableCell align="left"><b>Year</b></TableCell>
                <TableCell align="left"><b>Director</b></TableCell>
                <TableCell align="left"><b>Stars</b></TableCell>
                <TableCell align="left"><b>Review</b></TableCell>
                <TableCell  style = {{width: "400px"}} align="right" className = "justify-content-right align-items-center pb-0 pe-0">

                <Form onSubmit={handleSearchSubmit} className="row">

                  <FormGroup className="row d-flex justify-content-end align-items-center pe-0">
                    <div className = "col-3">
                      <Input 
                          type="select" 
                          value={searchType} 
                          onChange={(e) => setSearchType(e.target.value)}
                          className='pe-0 ps-2'>
                            <option value="title">Title</option>
                            <option value="year">Year</option>
                            <option value="id">ID</option>
                            <option value="all">All</option>
                      </Input>
                    </div>

                    <div className = "col-4 ms-0 ps-0">
                      <Input 
                        type="text" 
                        name="searchTerm" 
                        id="searchTerm" 
                        value={searchInput} 
                        onChange={handleSearch} placeholder="Search..." 
                      />
                    </div>

                    <Button className="col-auto me-2 pb-6 pb-2 align-items-center ps-3 pe-3" type="submit" color="danger">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-search-heart-fill" viewBox="0 0 16 16">
                        <path d="M6.5 13a6.474 6.474 0 0 0 3.845-1.258h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.008 1.008 0 0 0-.115-.1A6.471 6.471 0 0 0 13 6.5 6.502 6.502 0 0 0 6.5 0a6.5 6.5 0 1 0 0 13Zm0-8.518c1.664-1.673 5.825 1.254 0 5.018-5.825-3.764-1.664-6.69 0-5.018Z" />
                      </svg>
                    </Button>

                    <Button className="col-auto me-2 pb-6 pb-2 align-items-center ps-3 pe-3" onClick = {clear} color="danger">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-x-circle-fill" viewBox="0 0 16 16">
                      <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM5.354 4.646a.5.5 0 1 0-.708.708L7.293 8l-2.647 2.646a.5.5 0 0 0 .708.708L8 8.707l2.646 2.647a.5.5 0 0 0 .708-.708L8.707 8l2.647-2.646a.5.5 0 0 0-.708-.708L8 7.293 5.354 4.646z"/>
                    </svg>
                    </Button>

                    </FormGroup>
                  </Form>  

                </TableCell>

                <TableCell style={{ backgroundColor: "#212529", width: "2px"}} className='ps-0 pe-0'></TableCell>
              </TableRow>
            </TableHead>

            <TableBody>
              {filteredFilms.map((row) => (
                <TableRow
                  key={row.id}
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.id}
                  </TableCell>
                  <TableCell align="left">{row.title}</TableCell>
                  <TableCell align="left">{row.year}</TableCell>
                  <TableCell align="left">{row.director}</TableCell>
                  <TableCell align="left">{row.stars}</TableCell>
                  <TableCell align="left" colSpan={2}>{row.review}</TableCell>

                  <TableCell align="right">
                    <Button className="optionsShadow" title="Update" size="sm" color="white" tag={Link} to={"/films/" + row.id}>
                      <h5>&#9997;</h5>
                    </Button>


                    <Button className="optionsShadow" title="Delete" size="sm" color="white" starticon={<DeleteIcon />} onClick={() => remove(row.id)}>
                      <h5>&#10060;</h5>
                    </Button>

                  </TableCell>


                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
      { }
    </div>
  );
};

export default FilmTable;
