import React, { useState } from 'react';
import TextField from '@mui/material/TextField';

const Search = ({ groups, setGroups }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleInputChange = (event) => {
    if (props.films) {
      const value = event.target.value.toLowerCase();
      const filteredFilms = props.films.filter((film) => {
        return film.title.toLowerCase().includes(value) || film.year.toString().includes(value);
      });
      setFiltered(filteredFilms);
    }
  };

  const filteredGroups = groups.filter(
    (group) =>
      group.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      group.year.toString().includes(searchTerm)
  );

  return (
    <div>
      <TextField
        id="outlined-basic"
        label="Search by Title or Year"
        variant="outlined"
        value={searchTerm}
        onChange={handleChange}
      />
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          {filteredGroups.map((group) => (
            <tr key={group.id}>
              <td>{group.id}</td>
              <td>{group.title}</td>
              <td>{group.year}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Search;
