import React, { useState , createContext, useContext} from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Films from './Films';
import EditFilm from './CRUD';
import CRUD from './CRUD';


import { FormGroup, Input } from 'reactstrap';
import { useNavigate } from "react-router-dom";
import AppContext from './AppContext.js';

console.log("AppContext",AppContext.format);
const App = () => {

  return (
    <Router>
      <Routes>
        <Route exact path="/" element={<Films/>}/>
        <Route path='/films/:id' element={<EditFilm/>}/>
        <Route path='/films/add' element={<CRUD/>}/>
      </Routes>
    </Router>
  )
}

const Format = () => {
  //const [format, setFormat] = useState('format');
  const { format, setFormat } = useContext(AppContext);
  const navigate = useNavigate();

  const handleFormat = async (event) => {
    console.log("Dropdown: ", event.target.value);
    AppContext.format = event.target.value;
    //added 
    setFormat(event.target.value);
    await handleSubmit(event, event.target.value);
  };

  // changed this to call fetch everytime the dropdown is changed
  // added selected format to update value correctly + removed <Form> as this is done by handleFormat
  const handleSubmit = async (event, selectedFormat) => {
    let accept = "application/json";
    event.preventDefault();  

    if (selectedFormat === "xml")
    {
      console.log("xml");
      accept = "application/xml";
    }
    else if (selectedFormat === "text")
    {
      console.log("text");
      accept = "text/plain";
    }
    else 
    {
      console.log("json");
      accept = "application/json";
    }


   await fetch(`/FilmAPI/filmapi`, 
   {
      method: "GET",
      headers: {
        Accept: accept,
        "Content-Type": accept,
      },

    })


    navigate("/");

  };

  return (
    <div className = "ms-4 me-3 row pt-4"> 
      <FormGroup>
        <Input 
          type="select" 
          name="formatType" 
          value={format} 
          onChange={handleFormat}
        >
            <option value="json">JSON</option>
            <option value="xml">XML</option>
            <option value="text">Text</option>
        </Input>
      </FormGroup>
    </div>

  );
};

export { App,Format };
