import React, { useState} from 'react';
import '../css/App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Films from '../ui/Home';
import EditFilm from '../ui/FilmAddEdit';
import AddEdit from '../ui/FilmAddEdit';
import FormatContext from './FormatContext.js';

const App = () => {

    const [format, setFormat] = useState('json');

    return (
      <FormatContext.Provider value={{ format, setFormat }}>
        <Router>
        <Routes>
          <Route exact path="/" element={<Films/>}/>
          <Route path='/films/:id' element={<EditFilm/>}/>
          <Route path='/films/add' element={<AddEdit/>}/>
        </Routes>
      </Router>
      </FormatContext.Provider>
    )
  }

  export default App;
