import React, { useEffect, useState } from 'react';
import { Button, Container } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import DeleteIcon from '@mui/icons-material/Delete';
import CircularProgress from '@mui/material/CircularProgress';
import AppContext from './AppContext';

import './buttonStyles.css';

 // use this for search do search in this file


  //var format = 
  console.log("AppContext in list ",AppContext.format);

  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(false);

  const GetFilms = async(id, title, year, director, stars, review, format) =>
  {
    useEffect(() => {
      setLoading(true);
      //get use this for search

      let body = Request.body;
      let accept = "application/json";
      console.log("AppContext in remove func ",AppContext.format);
      format = AppContext.format

      if (format === "xml") {
        console.log("format === xml");
        accept = "application/xml";
        body = `<Film><id>${id}</id><title>${title}</title><year>${year}</year><director>${director}</director><stars>${stars}</stars><review>${review}</review></Film>`;
      } 

      else if (format === "text") {
        console.log("format === text");
        accept = "text/plain";
        const bodyAsText = `${title}#${year}#${director}#${stars}#${review}|`;
        body = bodyAsText;
      }

      else {
        console.log("format === json");
        accept = "application/json";
        body = JSON.stringify({'id': id, 'title': title, 'year':year, 'director':director, 'stars':stars, 'review':review});
      }

    fetch('FilmAPI/filmapi', {
      headers: {
        'Accept': accept,
        'Content-Type': accept

      },
      body:body
    })
    .then(data => {
      setGroups(data);
      setLoading(false);
    })
    }, []);





    // GET original
  //   fetch('FilmAPI/filmapi', {
  //       headers: {
  //         'Accept': 'application/json'
  //       }
  //     })
  //     .then(response => response.json())
  //     .then(data => {
  //       setGroups(data);
  //       setLoading(false);
  //     })
  // }, []);

  // delete

  // TRY 2

  const remove = async (id, format) => {
    let body = Request.body;
    let accept = "application/json";
    console.log("AppContext in remove func ",AppContext.format);
    format = AppContext.format
    if (format === "xml") {
      console.log("format === xml");
      accept = "application/xml";
      body = `<Film><id>${id}</id></Film>`;
    } 

    else if (format === "text") {
      console.log("format === text");
      accept = "text/plain";
      body = `${id}#`;
    }

    else {
      console.log("format === json");
      accept = "application/json";
      body = JSON.stringify({'id': id});
    }

    await fetch(`/FilmAPI/filmapi`, {
      method: 'DELETE',
      headers: {
        'Accept': accept,
        'Content-Type': accept
      },
      body: body
    }).then(() => {
      let updatedGroups = [...groups].filter(i => i.id !== id);
      setGroups(updatedGroups);
    });
  }

  if (loading) {

    return (
      <div className='d-flex justify-content-center d-flex align-items-center'>
        <CircularProgress color = "error"/>
      </div>
    );
  }

  return (
    <div>
      <AppNavbar />
      <Container fluid className = "pt-3">

        <TableContainer component={Paper} className='shadow'>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead
             sx={{
              "& th": {
                color: "white",
                backgroundColor: "#212529"
              }
            }}>
              <TableRow>
                <TableCell><b>ID</b></TableCell>
                <TableCell align="left"><b>Title</b></TableCell>
                <TableCell align="left"><b>Year</b></TableCell>
                <TableCell align="left"><b>Director</b></TableCell>
                <TableCell align="left"><b>Stars</b></TableCell>
                <TableCell align="left"><b>Review</b></TableCell>
                <TableCell align="left"></TableCell>
                <TableCell align="left"></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {groups.map((row) => (
                <TableRow
                  key={row.id}
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.id}
                  </TableCell>
                  <TableCell align="left">{row.title}</TableCell>
                  <TableCell align="left">{row.year}</TableCell>
                  <TableCell align="left">{row.director}</TableCell>
                  <TableCell align="left">{row.stars}</TableCell>
                  <TableCell align="left">{row.review}</TableCell>

                  <TableCell align="left">
                    <Button className="optionsShadow" title="Update" size="sm" color="white" tag={Link} to={"/films/" + row.id}>
                      <h5>&#9997;</h5>
                    </Button>
                  </TableCell>

                  <TableCell align="left">
                    <Button className="optionsShadow" title="Delete" size="sm" color="white" starticon={<DeleteIcon />} onClick={() => remove(row.id)}>
                      <h5>&#10060;</h5>
                    </Button>
                  </TableCell>


                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
      { }
    </div>
  );
};

export default Films;
