import React, { useState } from 'react';
import { Form, FormGroup, Input, Button } from 'reactstrap';


const SearchForm = ({ handleSearchTermChange, handleSubmit }) => {
  const [searchTerm, setSearchTerm] = useState('');
  
  

  return (
    <Form onSubmit={handleSubmit} className="inline-flex search-container justify-content-end row gy-6 pt-4">
      <FormGroup className="col-8 mb-4">
        <Input type="text" name="searchTerm" id="searchTerm" value={searchTerm} onChange={handleSearchTermChange} placeholder="Search" />
      </FormGroup>
      <Button className="col-auto mb-4 me-2 pb-6 pb-2 align-items-center ps-3 pe-3" type="submit" color="danger">
        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-search-heart-fill" viewBox="0 0 16 16">
          <path d="M6.5 13a6.474 6.474 0 0 0 3.845-1.258h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.008 1.008 0 0 0-.115-.1A6.471 6.471 0 0 0 13 6.5 6.502 6.502 0 0 0 6.5 0a6.5 6.5 0 1 0 0 13Zm0-8.518c1.664-1.673 5.825 1.254 0 5.018-5.825-3.764-1.664-6.69 0-5.018Z" />
        </svg>
      </Button>
      <div className="vr rounded pt-2 ms-3 mb-3 pe-0 ps-1 bg-black"></div>
    </Form>
  );

};


export default SearchForm;
