import React, { useEffect, useState } from 'react';
import { Button, Container } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import DeleteIcon from '@mui/icons-material/Delete';
import CircularProgress from '@mui/material/CircularProgress';
import AppContext from './AppContext';

import './buttonStyles.css';

 // use this for search do search in this file
const Films = () => {

  //var format = 
  console.log("AppContext in list ",AppContext.format);

  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(false);

  // get films

  const parseXml = (xmlString) => {
    const parser = new DOMParser();
    const xmlDoc = parser.parseFromString(xmlString, "application/xml");

    const films = Array.from(xmlDoc.getElementsByTagName("Film")).map(film => {
      return {
        id: parseInt(film.getElementsByTagName("id")[0].textContent, 10),
        title: film.getElementsByTagName("title")[0].textContent,
        year: film.getElementsByTagName("year")[0].textContent,
        director: film.getElementsByTagName("director")[0].textContent,
        stars: film.getElementsByTagName("stars")[0].textContent,
        review: film.getElementsByTagName("review")[0].textContent
      };
    });

    return films;
  };

  // Example XML structure
  const filmsxml = `
  <Films>
    <Film>
      <id>1</id>
      <title>Example Movie</title>
      <year>2022</year>
      <director>John Doe</director>
      <stars>Jane Doe, John Smith</stars>
      <review>Great movie!</review>
    </Film>
  </Films>`;

  // Use the parseXml function with the example XML structure
  const parsedFilms = parseXml(filmsxml);
  console.log(parsedFilms);
  //This will output the parsed films as a JavaScript object. You can customize the filmsxml string with the desired XML data, and then use the parseXml function to convert it into a JavaScript object.








  const parseText = (textString) => {
    // Assuming your text format is like this: "1#Movie Title,2#Another Movie"
    const films = textString.split("|").map(filmStr => {
      const [id, title, year, director, stars, review] = filmStr.split("#");
      return {
        id: parseInt(id, 10),
        title: title,
        year: year,
        director: director,
        stars: stars,
        review: review
      };
    });

    return films;
  };

  useEffect(() => {
    setLoading(true);

    const format = AppContext.format;
    let accept;

    if (format === "xml") {
      accept = "application/xml";
    } else if (format === "text") {
      accept = "text/plain";
    } else {
      accept = "application/json";
    }

    fetch('FilmAPI/filmapi', {
        headers: {
          'Accept': accept
        }
      })
      .then(response => {
        if (format === "json") {
          return response.json();
        } else if (format === "xml") {
          return response.text();
        } else {
          return response.text();
        }
      })
      .then(data => {
        if (format === "xml") {
          data = parseXml(data);
        } else if (format === "text") {
          data = parseText(data);
        }
        setGroups(data);
        setLoading(false);
      })
  }, []);


  // original
  // useEffect(() => {
  //   setLoading(true);
  //   //get use this for search
  //   fetch('FilmAPI/filmapi', {
  //       headers: {
  //         'Accept': 'application/json'
  //       }
  //     })
  //     .then(response => response.json())
  //     .then(data => {
  //       setGroups(data);
  //       setLoading(false);
  //     })
  // }, []);

  // original

  // delete

  // TRY 2

  const remove = async (id, format) => {
    let body = Request.body;
    let accept = "application/json";
    console.log("AppContext in remove func ",AppContext.format);
    format = AppContext.format
    if (format === "xml") {
      console.log("format === xml");
      accept = "application/xml";
      body = `<Film><id>${id}</id></Film>`;
    } 

    else if (format === "text") {
      console.log("format === text");
      accept = "text/plain";
      body = `${id}#`;
    }

    else {
      console.log("format === json");
      accept = "application/json";
      body = JSON.stringify({'id': id});
    }

    await fetch(`/FilmAPI/filmapi`, {
      method: 'DELETE',
      headers: {
        'Accept': accept,
        'Content-Type': accept
      },
      body: body
    }).then(() => {
      let updatedGroups = [...groups].filter(i => i.id !== id);
      setGroups(updatedGroups);
    });
  }


  // SEPARATE


  // XML Delete
  // const remove = async (id) => {
  //   await fetch(`/FilmAPI/filmapi`, {
  //     method: 'DELETE',
  //     headers: {
  //       'Accept': 'application/xml',
  //       'Content-Type': 'application/xml'
  //     },
  //     body: `<Film><id>${id}</id></Film>`
  //   }).then(() => {
  //     let updatedGroups = [...groups].filter(i => i.id !== id);
  //     setGroups(updatedGroups);
  //   });
  // }

  // Text Delete
  // const remove = async (id) => {
  //   const bodyAsText = `${id}#`;

  //   await fetch(`/FilmAPI/filmapi`, {
  //     method: 'DELETE',
  //     headers: {
  //       'Accept': 'text/plain',
  //       'Content-Type': 'text/plain'
  //     },
  //     body: bodyAsText
  //   }).then(() => {
  //     let updatedGroups = [...groups].filter(i => i.id !== id);
  //     setGroups(updatedGroups);
  //   });
  // }


  // original - json
  // const remove = async (id) => {
  //   await fetch(`/FilmAPI/filmapi`, {
  //     method: 'DELETE',
  //     headers: {
  //       'Accept': 'application/json',
  //       'Content-Type': 'application/json'
  //     },
  //     body: JSON.stringify({'id': id})
  //   }).then(() => {
  //     let updatedGroups = [...groups].filter(i => i.id !== id);
  //     setGroups(updatedGroups);
  //   });
  // }

  if (loading) {

    return (
      <div className='d-flex justify-content-center d-flex align-items-center'>
        <CircularProgress color = "error"/>
      </div>
    );
  }

  return (
    <div>
      <AppNavbar />
      <Container fluid className = "pt-3">

        <TableContainer component={Paper} className='shadow'>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead
             sx={{
              "& th": {
                color: "white",
                backgroundColor: "#212529"
              }
            }}>
              <TableRow>
                <TableCell><b>ID</b></TableCell>
                <TableCell align="left"><b>Title</b></TableCell>
                <TableCell align="left"><b>Year</b></TableCell>
                <TableCell align="left"><b>Director</b></TableCell>
                <TableCell align="left"><b>Stars</b></TableCell>
                <TableCell align="left"><b>Review</b></TableCell>
                <TableCell align="left"></TableCell>
                <TableCell align="left"></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {groups.map((row) => (
                <TableRow
                  key={row.id}
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.id}
                  </TableCell>
                  <TableCell align="left">{row.title}</TableCell>
                  <TableCell align="left">{row.year}</TableCell>
                  <TableCell align="left">{row.director}</TableCell>
                  <TableCell align="left">{row.stars}</TableCell>
                  <TableCell align="left">{row.review}</TableCell>

                  <TableCell align="left">
                    <Button className="optionsShadow" title="Update" size="sm" color="white" tag={Link} to={"/films/" + row.id}>
                      <h5>&#9997;</h5>
                    </Button>
                  </TableCell>

                  <TableCell align="left">
                    <Button className="optionsShadow" title="Delete" size="sm" color="white" starticon={<DeleteIcon />} onClick={() => remove(row.id)}>
                      <h5>&#10060;</h5>
                    </Button>
                  </TableCell>


                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Container>
      { }
    </div>
  );
};

export default Films;
