import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { Button, Container, Form, FormGroup, Input, Label } from "reactstrap";
import AppNavbar from "./AppNavbar";

const FilmEdit = () => {

  const initialFormState = {
    title: "",
    year: "",
    director: "",
    stars: "",
    review: "",
  };

  const [group, setGroup] = useState(initialFormState);
  const [formData, setFormData] = useState(initialFormState); // New formData state
  const navigate = useNavigate();
  const { id } = useParams();
  const [format, setFormat] = useState('format');

  // setting values through GET request
  useEffect(() => {
    if (id) {
      fetch(`/FilmAPI/filmapi?id=${id}`)
        .then((response) => response.json())
        .then((group) => {
          console.log(group);
          setGroup(group);
          setFormData(group); // Set the fetched data to formData state
        });
    }
  }, [id]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value }); // Update formData instead of group
  };

  const handleFormat = (event) => {
    setFormat(event.target.value);
  };

  const handleSubmit = async (event) => {
    let body = Request.body;
    let accept = "application/json";
    event.preventDefault();

    if (format === "xml") {
      console.log("XML Body");
      accept = "application/xml";
      const React = require('react');
      const ReactDomServer = require("react-dom/server")
      const Film = (group) => React.createElement("Film", group);
      const elementXML = ReactDomServer.renderToStaticMarkup(<Film><title>{group.title}</title><year>{group.year}</year><director>{group.director}</director><stars>{group.stars}</stars><review>{group.review}</review></Film>)
      body = elementXML;
    }

    else if (format === "text") {
      accept = "text/plain";
      const { title, year, director, stars, review } = group;
      const bodyAsText = `${title}#${year}#${director}#${stars}#${review}|`;
      body = bodyAsText;
    }

    else {
      body = JSON.stringify(group);
    }

    if (id) {
      await fetch(`/FilmAPI/filmapi`, {
        method: "PUT",
        headers: {
          Accept: accept,
          "Content-Type": accept,
        },
        body: body,
      });
    } else {
      await fetch("/FilmAPI/filmapi", {
        method: "POST",
        headers: {
          Accept: accept,
          "Content-Type": accept,
        },
        body: body,
      });
    }

    setGroup(initialFormState);
    setFormData(initialFormState); // Reset formData state
    navigate("/");
  };

  const ptitle = <h2 className="d-flex justify-content-center pt-5 display-6">{id ? "Edit Film" : "Add Film"}</h2>;
  return (
    <div>
      <AppNavbar />
      <Container>
        {ptitle}
        <Form onSubmit={handleSubmit} id="form">
          <FormGroup>
            <label>
              Select Format
              <select>
                name="format"
                onChange={handleFormat}
                value={format}
                className="ms-4"
                                <option value="json">JSON</option>
                <option value="xml">XML</option>
                <option value="text">Text</option>
              </select>
            </label>
          </FormGroup>

          <FormGroup>
            <Label for="title">Title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              value={formData.title || ''}
              onChange={handleChange}
              autoComplete="title"
            />
          </FormGroup>

          <FormGroup>
            <Label for="year">Year</Label>
            <Input
              type="text"
              name="year"
              id="year"
              value={formData.year || ""}
              onChange={handleChange}
              autoComplete="year"
            />
          </FormGroup>

          <FormGroup>
            <Label for="director">Director</Label>
            <Input
              type="text"
              name="director"
              id="director"
              value={formData.director || ""}
              onChange={handleChange}
              autoComplete="director"
            />
          </FormGroup>

          <FormGroup>
            <Label for="stars">Stars</Label>
            <Input
              type="text"
              name="stars"
              id="stars"
              value={formData.stars || ""}
              onChange={handleChange}
              autoComplete="stars"
            />
          </FormGroup>

          <FormGroup>
            <Label for="review">Review</Label>
            <Input
              type="text"
              name="review"
              id="review"
              value={formData.review || ""}
              onChange={handleChange}
              autoComplete="review"
            />
          </FormGroup>

          <FormGroup>
            <Button color="danger" type="submit">
              Save
            </Button>{" "}
            <Button color="danger" tag={Link} to="/">
              Cancel
            </Button>
          </FormGroup>
        </Form>
      </Container>
    </div>
  );
};

export default FilmEdit;
