import React, { useState , createContext} from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Films from './Films';
import EditFilm from './CRUD';
import CRUD from './CRUD';


import { Form, FormGroup, Input } from 'reactstrap';
import { useNavigate } from "react-router-dom";
import AppContext from './AppContext.js';

console.log("AppContext",AppContext.format);

const App = () => {

  const [format, setFormat] = useState('json');

  const handleFormatChange = (newFormat) => {
    setFormat(newFormat);
  };


  return (
    <Router>
      <Routes>
        {/* <Route exact path="/" element={<Films/>}/> */}
        <Route exact path="/" element={<Films format={format} />} />
        <Route path='/films/:id' element={<EditFilm/>}/>
        <Route path='/films/add' element={<CRUD/>}/>
      </Routes>
    </Router>
  )
}

const Format = ({onFormatChange}) => {
  const [format, setFormat] = useState('format');
  const navigate = useNavigate();

  const handleFormat = (event) => {
    console.log("Dropdown: ",event.target.value);
    //AppContext.format=event.target.value;
    const newFormat = event.target.value; 
    setFormat(newFormat);
    onFormatChange(newFormat);
    //onFormatChange(event.target.value);
    //setFormat(event.target.value);
  };

  const handleSubmit = async (event) => {
    let accept = "application/json";
    event.preventDefault();  
    console.log(this.state);
    if (format === "xml")
    {
      console.log("xml");
      accept = "application/xml";
    }
    else if (format === "text")
    {
      console.log("text");
      accept = "text/plain";
    }
    else 
    {
      console.log("json");
      accept = "application/json";
    }

   await fetch(`/FilmAPI/filmapi`, {
      method: "GET",
      headers: {
        Accept: accept,
        "Content-Type": accept,
      },

    })

    navigate("/");

  };

  return (
    <Form onSubmit={handleSubmit} className = "ms-4 me-3 row pt-4"> 
      <FormGroup>
        <Input 
          type="select" 
          name="formatType" 
          value={format} 
          onChange={handleFormat}
        >
            <option value="json">JSON</option>
            <option value="xml">XML</option>
            <option value="text">Text</option>
        </Input>
      </FormGroup>
    </Form>
  );
};

export { App,Format };
