import React, { useState } from 'react';
import { BrowserRouter,  Router, Routes, Route } from 'react-router-dom';
import Home from '../ui/Home';
import EditFilm from '../ui/FilmAddEdit';
import AddEdit from '../ui/FilmAddEdit';
import FormatContext from '../config/FormatContext.js';

/**
 * The main App component of the application.
 *
 * Sets up the format context for the data format (defaults to 'json'). 
 * It also sets up routing for the application
 *
 * @returns {React.Component} The App component which includes routing and the context provider for the application.
 */
const App = () => {
  const [format, setFormat] = useState('json');

  return (
    <BrowserRouter>
      <FormatContext.Provider value={{ format, setFormat }}>

          <Routes>
            <Route exact path="/" element={<Home/>}/>
            <Route path='/films/:id' element={<EditFilm/>}/>
            <Route path='/films/add' element={<AddEdit/>}/>
          </Routes>

      </FormatContext.Provider>
    </BrowserRouter>
  )
}

export default App;
