import React, { useEffect, useState, useContext } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { Button, Container, Form, FormGroup, Input, Label } from "reactstrap";
import AppNavbar from "./AppNavbar";
import AppContext from './AppContext';

// add and edit

const FilmEdit = () => {

  // add form
  const initialFormState = {
    id: "",
    title: "",
    year: "",
    director: "",
    stars: "",
    review: "",
  };

  const [group, setGroup] = useState(initialFormState);
  const navigate = useNavigate();
  const { id } = useParams();
  //const [format, setFormat] = useState('format');
  const { format } = useContext(AppContext);

  // setting values

  useEffect(() => {
    fetch(`/FilmAPI/filmapi?id=${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log('Film ID:', id);
        console.log("Setting Values...\n",data)
        // setGroup({
        //   id: group.id,
        //   title: group.title,
        //   year: group.year,
        //   director: group.director,
        //   stars: group.stars,
        //   review: group.review,
        // });


        // hardcoded test
        // setGroup({
        //   id: "1",
        //   title: "Test Title",
        //   year: "2000",
        //   director: "Test Director",
        //   stars: "Test Stars",
        //   review: "Test Review",
        // });

        const film = data[0]; 

        setGroup({
          id: film.id,
          title: film.title,
          year: film.year,
          director: film.director,
          stars: film.stars,
          review: film.review,
        });


      });
  }, [id, format]); 


  const handleChange = (event) => {
    const { name, value } = event.target;
    setGroup({ ...group, [name]: value });

  };

  // const handleFormat = (event) => {
  //   setFormat(event.target.value);
  // };

  // switch formats
  const handleSubmit = async (event) => {
    let body = Request.body;
    let accept = "application/json";
    event.preventDefault();  

    // validation

    if (!group.title || !group.year || !group.director || !group.stars || !group.review) {
      alert('Please fill in all required fields.');
      return;
    }

    if (format === "xml")
    {
      // edit
      console.log("XML Body");
      accept = "application/xml";
      const React = require('react');
      const ReactDomServer = require("react-dom/server")
      const Film = (group) => React.createElement("Film", group);
      var elementXML = ReactDomServer.renderToStaticMarkup(<Film><id>{group.id}</id><title>{group.title}</title><year>{group.year}</year><director>{group.director}</director><stars>{group.stars}</stars><review>{group.review}</review></Film>)
      body = elementXML;

      // add
      if (!id) {
        const FilmWithoutId = (group) =>
          React.createElement("Film", { ...group, id: undefined });
        elementXML = ReactDomServer.renderToStaticMarkup(
          <FilmWithoutId>
            <title>{group.title}</title>
            <year>{group.year}</year>
            <director>{group.director}</director>
            <stars>{group.stars}</stars>
            <review>{group.review}</review>
          </FilmWithoutId>
        );
      }
    }

    else if (format === "text")
    {
      // edit 

      accept = "text/plain";
      const { title, year, director, stars, review } = group;
      const bodyAsText = `${id}#${title}#${year}#${director}#${stars}#${review}|`;
      body = bodyAsText;

      // add

      if (!id) {
        const { title, year, director, stars, review } = group;
        body = `#${title}#${year}#${director}#${stars}#${review}|`;
        console.log(group.year);
      }
    }

    else
    {
      // edit
      body = JSON.stringify(group) ;

      // add
      if (!id) {
        const groupWithoutId = { ...group };
        delete groupWithoutId.id;

        body = JSON.stringify(groupWithoutId);
      }
    }


    // edit
    if (id) {
    await fetch(`/FilmAPI/filmapi`, {
        method: "PUT",
        headers: {
          Accept: accept,
          "Content-Type": accept,
        },
        body: body,
      });
    } 

    // add
    else {
      await fetch("/FilmAPI/filmapi", {
        method: "POST",
        headers: {
          Accept: accept,
          "Content-Type": accept,
        },
        body: body,
      });
    }

    setGroup(initialFormState);
    navigate("/");
  };

  const ptitle = <h2 className="d-flex justify-content-center pt-5 display-6">{id ? "Edit Film" : "Add Film"}</h2>;
  return (
    <div>
      <AppNavbar />
      <Container>
        {ptitle}
        <Form onSubmit={handleSubmit} id = "form" className="was-validated">

          {id && (
          <FormGroup>
            <Label for="id">ID</Label>
            <Input
              type="text"
              name="id"
              id="id"
              value={group.id || ""}
              onChange={handleChange}
              autoComplete="id"
              readOnly
              style={{ backgroundColor: '#e6e6e6' }}
              className="required"
            />
          </FormGroup>
        )}

          <FormGroup>
            <Label for="title">Title</Label>
            <Input
              type="text"
              name="title"
              id="title"
              value={group.title || ""}
              onChange={handleChange} 
              autoComplete="title"
              className="required"
            />
          </FormGroup>

          <FormGroup>
            <Label for="year">Year</Label>
            <Input
              type="text"
              name="year"
              id="year"
              value={group.year || ""}
              onChange={handleChange}
              autoComplete="year"
              className="required"
              pattern = "^[0-9]{4}$"
            />
          </FormGroup>

          <FormGroup>
            <Label for="director">Director</Label>
            <Input
              type="text"
              name="director"
              id="director"
              value={group.director || ""}
              onChange={handleChange}
              autoComplete="director"
              className="required"
            />
          </FormGroup>

          <FormGroup>
            <Label for="stars">Stars</Label>
            <Input
              type="text"
              name="stars"
              id="stars"
              value={group.stars || ""}
              onChange={handleChange}
              autoComplete="stars"
              className="required"
            />
          </FormGroup>

          <FormGroup className="FormGroup">
            <Label for="review">Review</Label>
            <Input
              type = "textarea"
              name="review"
              id="review"
              value={group.review || ""}
              onChange={handleChange}
              autoComplete="review"
              rows = "6"
              className="required"
            />
          </FormGroup>

          <FormGroup className="pt-3">
            <Button color="danger" type="submit" className="me-4">
              Save
            </Button>{" "}
            <Button color="danger" tag={Link} to="/">
              Cancel
            </Button>
          </FormGroup>
        </Form>
      </Container>
    </div>
  );
};

export default FilmEdit;
