import { createContext } from 'react';

/**
 * FormatContext is a React context for sharing format state and updater function across the application.
 *
 * @type {React.Context}
 * @property {string} format - The current data format ('json' by default).
 * @property {function} setFormat - A function to update the format state.
 */
const FormatContext = createContext({
  format: 'json',
  setFormat: () => {},
});

export default FormatContext;
