import React, { useEffect, useState, useContext } from 'react';
import { Button, Container, Form, FormGroup, Input } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import DeleteIcon from '@mui/icons-material/Delete';
import CircularProgress from '@mui/material/CircularProgress';
import AppContext from './AppContext';

import './buttonStyles.css';

 // use this for search do search in this file
const Films = () => {

  const { format } = useContext(AppContext);
  console.log("Fetching Films in ",format);

  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(false);

  // search

  const [searchInput, setSearchInput] = useState('');
  const [filteredFilms, setFilteredFilms] = useState([]);

  const handleSearchTermChange = (e) => {
    setSearchInput(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const lowerCaseSearch = searchInput.toLowerCase();

    const filtered = groups.filter(
      (film) =>
        film.title.toLowerCase().includes(lowerCaseSearch) ||
        film.year.toString().includes(lowerCaseSearch)
    );

    setFilteredFilms(filtered);
  };

  // clear search 

  const clear = () => {
    setSearchInput('');
    setFilteredFilms(groups);
  };

  // get films and parse for the table in 3 formats

  useEffect(() => {
    const fetchFilms = async (format) => {
      setLoading(true);

      let accept = "application/json";
      if (format === "xml") {
        accept = "application/xml";
      } else if (format === "text") {
        accept = "text/plain";
      }

      fetch("FilmAPI/filmapi", {
        headers: {
          Accept: accept,
        },
        mode: "cors",
      })
        .then(async (response) => {
          // NaN
          if (format === "text") {
            const text = await response.text();
            const films = text.trim().split("|");
            const data = films.map((film) => {
              const [
                id,
                title,
                year,
                director,
                stars,
                review,
              ] = film.split("#");
              return {
                id: isNaN(parseInt(id)) ? id : parseInt(id),
                title,
                year: isNaN(parseInt(year)) ? year : parseInt(year),
                director,
                stars,
                review,
              };
            });
            console.log("Text Table");
            return data;


          } else if (format === "xml") {
            const xmlData = await response.text();
            const parser = new DOMParser();
            const xmlDoc = parser.parseFromString(xmlData, "application/xml");

            const films = xmlDoc.getElementsByTagName("Film");
            const data = Array.from(films).map((film) => {

              return {
                id: parseInt(film.getElementsByTagName("id")[0].textContent),
                title: film.getElementsByTagName("title")[0].textContent,
                year: film.getElementsByTagName("year")[0].textContent,
                director: film.getElementsByTagName("director")[0].textContent,
                stars: film.getElementsByTagName("stars")[0].textContent,
                review: film.getElementsByTagName("review")[0].textContent,
              };
            });
            console.log("XML Table");
            return data;
          } 
          else {
            console.log("JSON Table");
            return response.json();
          }
        })
        .then((data) => {
          setGroups(data);
          setFilteredFilms(data);
          setLoading(false);

        });
    };

    fetchFilms(format);
  }, [format]);

  // original
  // useEffect(() => {
  //   setLoading(true);
  //   //get use this for search
  //   fetch('FilmAPI/filmapi', {
  //       headers: {
  //         'Accept': 'application/json'
  //       }
  //     })
  //     .then(response => response.json())
  //     .then(data => {
  //       setGroups(data);
  //       setLoading(false);
  //     })
  // }, []);
  // original

  // delete

  // TRY 2

  const remove = async (id, format) => {
    let body = Request.body;
    let accept = "application/json";
    console.log("AppContext in remove func ",AppContext.format);
    format = AppContext.format
    if (format === "xml") {
      console.log("format === xml");
      accept = "application/xml";
      body = `<Film><id>${id}</id></Film>`;
    } 

    else if (format === "text") {
      console.log("format === text");
      accept = "text/plain";
      body = `${id}#`;
    }

    else {
      console.log("format === json");
      accept = "application/json";
      body = JSON.stringify({'id': id});
    }

    await fetch(`/FilmAPI/filmapi`, {
      method: 'DELETE',
      headers: {
        'Accept': accept,
        'Content-Type': accept
      },
      body: body
    }).then(() => {
      let updatedGroups = [...groups].filter(i => i.id !== id);
      setGroups(updatedGroups);
    });


  }


  if (loading) {

    return (
      <div className='d-flex justify-content-center d-flex align-items-center'>
        <CircularProgress color = "error"/>
      </div>
    );
  }

  return (
    <div>
      <AppNavbar />
      <Container fluid className = "pt-3">

      {/* Search */}
